# Read thje txt file in ./postScript/fixed/generated.txt and convert it to a matrix
# Take into account the txt file has lines and columns separated by spaces

# Read the file
file = open('./postScript/fixed/generated_sample_0.txt', 'r')
lines = file.readlines()
file.close()

# Convert the file to a matrix
matrix = []
for line in lines:
    matrix.append([int(x) for x in line.split()])
matrix_array = np.array(matrix)


plt.imshow(matrix_array, cmap='binary', interpolation='nearest', origin='lower')
plt.title('Binary Matrix (0,0 at Lower-Left)')
plt.show()